mapper.select:
SELECT * FROM post_info WHERE pi_board_seq = #{board} AND pi_title LIKE #{keyword}
ORDER BY pi_seq DESC 
LIMIT 15 OFFSET #{offset}


SELECT a.*, b.ui_name
FROM comment_info a
INNER JOIN user_info b
ON a.ci_ui_seq = b.ui_seq
WHERE ci_pi_seq = #{board_seq}
LIMIT 10 OFFSET #{offset}

mapper.update:
UPDATE post_good_bad
SET pgb_good_bad = #{good_bad}
WHERE pgb_user_seq = #{user_seq} AND pgb_post_seq=#{post_seq}

UPDATE comment_good_bad
SET cgb_good_bad = #{good_bad}
WHERE cgb_user_seq = #{user_seq} AND cgb_com_seq=#{post_seq}
